import React, { useContext } from "react";
import { CartContext } from "../context/CartContext";

function ProductCard({ item }) {
  const { addToCart } = useContext(CartContext);
  const [qty, setQty] = React.useState(1);
  const handleQtyChange = (e) => {
    const value = Math.max(1, Number(e.target.value));
    setQty(value);
  };
  return (
    <div style={{
      border: "1px solid #ccc", padding: "10px", margin: "10px",
      width: "200px", borderRadius: "8px"
    }}>
      <img src={item.image} alt={item.name} width="100%" />
      <h3>{item.name}</h3>
      <p>â‚¹{item.price}</p>
      <div style={{ display: "flex", alignItems: "center", marginBottom: 8 }}>
        <button onClick={() => setQty(qty > 1 ? qty - 1 : 1)} style={{ padding: "2px 8px", fontSize: 18, borderRadius: 4, border: "1px solid #ccc", background: "#f5f5f5", cursor: "pointer" }}>-</button>
        <input type="number" min={1} value={qty} onChange={handleQtyChange} style={{ width: 40, textAlign: "center", margin: "0 8px", borderRadius: 4, border: "1px solid #ccc" }} />
        <button onClick={() => setQty(qty + 1)} style={{ padding: "2px 8px", fontSize: 18, borderRadius: 4, border: "1px solid #ccc", background: "#f5f5f5", cursor: "pointer" }}>+</button>
      </div>
      <button onClick={() => addToCart({ ...item, qty })}>Add to Cart</button>
    </div>
  );
}


export default ProductCard;
