  // Combo offer logic: if Vanilla Ice Cream is in cart, show offer for Dark Chocolate at â‚¹80
  const hasVanillaIceCream = cartItems.some(item => item.name && item.name.toLowerCase().includes("vanilla ice cream"));
  const hasComboDarkChocolate = cartItems.some(item => item.id === 20001); // unique id for combo chocolate
  const darkChocolateCombo = {
    id: 20001,
    name: "Dark Chocolate (Combo Offer)",
    price: 80,
    image: "http://www.bestherbalhealth.com/wp-content/uploads/2015/02/Health-Benefits-of-Dark-Chocolate.jpg",
    category: "chocolate"
  };
import React, { useContext } from "react";
import { CartContext } from "../context/CartContext";

function Cart() {
  const { cartItems, removeFromCart } = useContext(CartContext);

  const total = cartItems.reduce((sum, item) => sum + item.price * item.qty, 0);

  // Combo offer logic: if Chocolate Ice Cream is in cart, show offer for Belgian Chocolate at â‚¹90
  const hasChocolateIceCream = cartItems.some(item => item.name && item.name.toLowerCase().includes("chocolate ice cream"));
  const hasComboBelgianChocolate = cartItems.some(item => item.id === 30001); // unique id for combo chocolate
  const belgianChocolateCombo = {
    id: 30001,
    name: "Belgian Chocolate (Combo Offer)",
    price: 90,
    image: "https://tse1.mm.bing.net/th/id/OIP.5kletYVpG-GaGnJtkmuwxQHaE8?rs=1&pid=ImgDetMain&o=7&rm=3",
    category: "chocolate"
  };
  const { addToCart } = useContext(CartContext);

  return (
    <div className="cart-container" style={{ maxWidth: 600, margin: "40px auto", background: "#fff", borderRadius: 12, boxShadow: "0 2px 8px rgba(0,0,0,0.1)", padding: 24 }}>
      <h2 style={{ textAlign: "center", marginBottom: 24 }}>ðŸ›’ Your Cart</h2>
      {cartItems.length === 0 ? (
        <p style={{ textAlign: "center", color: "#888" }}>No items in cart.</p>
      ) : (
        <div>
          {hasChocolateIceCream && !hasComboBelgianChocolate && (
            <div style={{ background: "#e6f7ff", borderRadius: 8, padding: 16, marginBottom: 20, textAlign: "center", boxShadow: "0 1px 4px rgba(0,0,0,0.05)" }}>
              <h4 style={{ margin: 0 }}>ðŸŽ‰ Combo Offer!</h4>
              <p style={{ margin: "8px 0" }}>Buy <b>Chocolate Ice Cream</b> and get <b>Belgian Chocolate</b> for just <span style={{ color: "#ff4d4f" }}>â‚¹90</span>!</p>
              <button onClick={() => addToCart(belgianChocolateCombo)} style={{ background: "#52c41a", color: "#fff", border: "none", borderRadius: 6, padding: "8px 16px", cursor: "pointer", fontWeight: 500 }}>Add Combo Chocolate</button>
            </div>
          )}
          {hasVanillaIceCream && !hasComboDarkChocolate && (
            <div style={{ background: "#f0f5ff", borderRadius: 8, padding: 16, marginBottom: 20, textAlign: "center", boxShadow: "0 1px 4px rgba(0,0,0,0.05)" }}>
              <h4 style={{ margin: 0 }}>ðŸŽ‰ Combo Offer!</h4>
              <p style={{ margin: "8px 0" }}>Buy <b>Vanilla Ice Cream</b> and get <b>Dark Chocolate</b> for just <span style={{ color: "#ff4d4f" }}>â‚¹80</span>!</p>
              <button onClick={() => addToCart(darkChocolateCombo)} style={{ background: "#141414", color: "#fff", border: "none", borderRadius: 6, padding: "8px 16px", cursor: "pointer", fontWeight: 500 }}>Add Combo Dark Chocolate</button>
            </div>
          )}
          {cartItems.map((item) => (
            <div key={item.id} style={{ display: "flex", alignItems: "center", borderBottom: "1px solid #eee", padding: "16px 0" }}>
              <img src={item.image} alt={item.name} style={{ width: 80, height: 80, objectFit: "cover", borderRadius: 8, marginRight: 20, boxShadow: "0 1px 4px rgba(0,0,0,0.08)" }} />
              <div style={{ flex: 1 }}>
                <h4 style={{ margin: "0 0 8px 0" }}>{item.name}</h4>
                <p style={{ margin: 0, color: "#555" }}>Quantity: <b>{item.qty}</b></p>
                <p style={{ margin: "4px 0 0 0", color: "#555" }}>Price: â‚¹{item.price} Ã— {item.qty} = <b>â‚¹{item.price * item.qty}</b></p>
              </div>
              <button onClick={() => removeFromCart(item.id)} style={{ background: "#ff4d4f", color: "#fff", border: "none", borderRadius: 6, padding: "8px 16px", cursor: "pointer", fontWeight: 500 }}>Remove</button>
            </div>
          ))}
          <h3 style={{ textAlign: "right", marginTop: 24, color: "#222" }}>Total: â‚¹{total}</h3>
        </div>
      )}
    </div>
  );
}

export default Cart;
