import React, { useContext } from "react";
import { CartContext } from "../context/CartContext";

function Payment() {
  const [method, setMethod] = React.useState("");
  const [card, setCard] = React.useState({ number: "", name: "", expiry: "", cvv: "" });
  const [upi, setUpi] = React.useState("");
  const { cartItems } = useContext(CartContext);

  // Calculate total amount from cart
  const totalAmount = cartItems.reduce((sum, item) => sum + item.price * item.qty, 0);
  const name = card.name || upi || "Customer Name";

  // Helper to send payment data to backend
  const handlePayment = async (paymentData) => {
    try {
      const res = await fetch('http://localhost:5000/api/payment', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ ...paymentData, amount: totalAmount, items: cartItems })
      });
      const data = await res.json();
      if (data.success) {
        window.location.href = '/payment-success';
      } else {
        window.location.href = '/payment-failed';
      }
    } catch (err) {
      window.location.href = '/payment-failed';
    }
  };

  return (
    <div style={{
      minHeight: "100vh",
      background: "linear-gradient(135deg,#fbecc4 0%,#f8fafc 100%)",
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      justifyContent: "center",
      padding: "0 0 60px 0"
    }}>
      <div style={{
        maxWidth: 900,
        width: "100%",
        background: "linear-gradient(90deg,#ffe29f 0%,#fffbe6 100%)",
        borderRadius: 32,
        boxShadow: "0 8px 32px rgba(255,174,94,0.10)",
        padding: "56px 32px 32px 32px",
        marginTop: 60,
        marginBottom: 40,
        textAlign: "center"
      }}>
        <h1 style={{
          fontSize: 54,
          fontWeight: 900,
          color: "#d4380d",
          marginBottom: 18,
          letterSpacing: 2,
          textShadow: "0 2px 12px #ffe29f"
        }}>
          Payment Options
        </h1>
        <p style={{
          fontSize: 24,
          color: "#222",
          marginBottom: 32,
          fontWeight: 500
        }}>
          Select your preferred payment method:
        </p>
        <div style={{ display: "flex", justifyContent: "center", gap: 32, marginBottom: 32 }}>
          <button style={{ background: "linear-gradient(90deg,#d4380d 0%,#ffb347 100%)", color: "#fff", border: "none", borderRadius: 18, padding: "24px 32px", fontWeight: 700, fontSize: 22, minWidth: 180, boxShadow: "0 2px 8px #b2ebf2" }} onClick={() => setMethod("card")}>Pay with Card</button>
          <button style={{ background: "linear-gradient(90deg,#52c41a 0%,#b2ebf2 100%)", color: "#fff", border: "none", borderRadius: 18, padding: "24px 32px", fontWeight: 700, fontSize: 22, minWidth: 180, boxShadow: "0 2px 8px #b2ebf2" }} onClick={() => setMethod("upi")}>Pay with UPI</button>
          <button style={{ background: "linear-gradient(90deg,#ffe29f 0%,#fbecc4 100%)", color: "#d4380d", border: "none", borderRadius: 18, padding: "24px 32px", fontWeight: 700, fontSize: 22, minWidth: 180, boxShadow: "0 2px 8px #ffe29f" }} onClick={() => setMethod("cod")}>Cash on Delivery</button>
        </div>
        {method === "card" && (
          <form style={{ textAlign: "left", marginTop: 18 }} onSubmit={e => {
            e.preventDefault();
            handlePayment({ name: card.name, amount: totalAmount });
          }}>
            <h3 style={{ color: "#d4380d", fontWeight: 700, marginBottom: 18 }}>Card Details</h3>
            <input type="text" placeholder="Card Number" required maxLength={16} value={card.number} onChange={e => setCard({ ...card, number: e.target.value })} style={{ width: "100%", padding: 12, marginBottom: 12, borderRadius: 8, border: "1.5px solid #d4380d", fontSize: 18 }} />
            <input type="text" placeholder="Name on Card" required value={card.name} onChange={e => setCard({ ...card, name: e.target.value })} style={{ width: "100%", padding: 12, marginBottom: 12, borderRadius: 8, border: "1.5px solid #d4380d", fontSize: 18 }} />
            <div style={{ display: "flex", gap: 12 }}>
              <input type="text" placeholder="MM/YY" required maxLength={5} value={card.expiry} onChange={e => setCard({ ...card, expiry: e.target.value })} style={{ flex: 1, padding: 12, marginBottom: 12, borderRadius: 8, border: "1.5px solid #d4380d", fontSize: 18 }} />
              <input type="password" placeholder="CVV" required maxLength={3} value={card.cvv} onChange={e => setCard({ ...card, cvv: e.target.value })} style={{ flex: 1, padding: 12, marginBottom: 12, borderRadius: 8, border: "1.5px solid #d4380d", fontSize: 18 }} />
            </div>
            <button type="submit" style={{ background: "linear-gradient(90deg,#d4380d 0%,#ffb347 100%)", color: "#fff", border: "none", borderRadius: 10, padding: "12px 32px", fontWeight: 700, fontSize: 18, cursor: "pointer", marginTop: 12 }}>Pay Now</button>
          </form>
        )}
        {method === "upi" && (
          <form style={{ textAlign: "left", marginTop: 18 }} onSubmit={e => {
            e.preventDefault();
            handlePayment({ name: upi, amount: totalAmount });
          }}>
            <h3 style={{ color: "#52c41a", fontWeight: 700, marginBottom: 18 }}>UPI Payment</h3>
            <input type="text" placeholder="Enter UPI ID" required value={upi} onChange={e => setUpi(e.target.value)} style={{ width: "100%", padding: 12, marginBottom: 12, borderRadius: 8, border: "1.5px solid #52c41a", fontSize: 18 }} />
            <button type="submit" style={{ background: "linear-gradient(90deg,#52c41a 0%,#b2ebf2 100%)", color: "#fff", border: "none", borderRadius: 10, padding: "12px 32px", fontWeight: 700, fontSize: 18, cursor: "pointer", marginTop: 12 }}>Pay Now</button>
          </form>
        )}
        {method === "cod" && (
          <div style={{ marginTop: 18 }}>
            <h3 style={{ color: "#d4380d", fontWeight: 700, marginBottom: 18 }}>Cash on Delivery Selected</h3>
            <p style={{ fontSize: 18, color: "#555" }}>You can pay when your order is delivered.</p>
            <button
              style={{ background: "linear-gradient(90deg,#d4380d 0%,#ffb347 100%)", color: "#fff", border: "none", borderRadius: 10, padding: "12px 32px", fontWeight: 700, fontSize: 18, cursor: "pointer", marginTop: 12 }}
              onClick={() => handlePayment({ name: "Cash on Delivery", amount: totalAmount })}
            >Confirm Order</button>
          </div>
        )}
      </div>
    </div>
  );
}
// Example of sending payment data to backend
fetch('http://localhost:5000/api/payment', {
  method: 'POST',
  headers: { 'Content-Type': 'application/json' },
  body: JSON.stringify({
    name: 'Customer Name',
    amount: 100 // Replace with actual amount
  })
})
.then(res => res.json())
.then(data => {
  if (data.success) {
    // Show payment success page
  } else {
    // Show payment failed page
  }
});

export default Payment;
