import ProductList from "../components/ProductList";

function Shop() {
  return (
    <div style={{
      minHeight: "100vh",
      background: "linear-gradient(135deg,#ffe29f 0%,#ffa99f 50%,#fffde4 100%)",
      padding: "40px 0"
    }}>
      <div style={{ maxWidth: 1100, margin: "0 auto", padding: "0 24px" }}>
        <h1 style={{ textAlign: "center", color: "#d4380d", fontSize: 44, fontWeight: 800, marginBottom: 48, letterSpacing: 2, textShadow: "0 2px 8px #fffbe6" }}>Welcome to Sweet Shop</h1>
        <div style={{ display: "flex", flexWrap: "wrap", gap: 36, justifyContent: "center" }}>
          <div style={{ flex: "1 1 420px", background: "#fff", borderRadius: 20, boxShadow: "0 4px 24px rgba(0,0,0,0.10)", padding: 36, marginBottom: 36, minWidth: 340 }}>
            <h2 style={{ color: "#ff4d4f", fontSize: 32, fontWeight: 700, marginBottom: 22, textAlign: "center", letterSpacing: 1, textShadow: "0 2px 8px #ffe29f" }}>üç¶ Ice Creams</h2>
            <ProductList category="icecream" />
          </div>
          <div style={{ flex: "1 1 420px", background: "#fff", borderRadius: 20, boxShadow: "0 4px 24px rgba(0,0,0,0.10)", padding: 36, marginBottom: 36, minWidth: 340 }}>
            <h2 style={{ color: "#d4380d", fontSize: 32, fontWeight: 700, marginBottom: 22, textAlign: "center", letterSpacing: 1, textShadow: "0 2px 8px #ffa99f" }}>üç´ Chocolates</h2>
            <ProductList category="chocolate" />
          </div>
        </div>
      </div>
    </div>
  );
}

export default Shop;
