import React, { useContext } from "react";
import { CartContext } from "../context/CartContext";

function ProductCard({ item }) {
  const { cartItems, addToCart } = useContext(CartContext);
  // If product is ice cream and any chocolate is in cart, show combo offer
  const isIceCream = item.category && item.category.toLowerCase().includes("icecream");
  const hasChocolate = cartItems.some(
    (cartItem) => cartItem.category && cartItem.category.toLowerCase().includes("chocolate")
  );
  let comboPrice = null;
  if (isIceCream && hasChocolate) {
    // Example: discount 20% for combo
    comboPrice = Math.round(item.price * 0.8);
  }
  return (
    <div style={{
      border: "1px solid #ccc", padding: "10px", margin: "10px",
      width: "200px", borderRadius: "8px"
    }}>
      <img src={item.image} alt={item.name} width="100%" />
      <h3>{item.name}</h3>
      <p>â‚¹{comboPrice ? <><span style={{textDecoration:'line-through',color:'#888'}}>{item.price}</span> <span style={{color:'#ff4d4f'}}>{comboPrice}</span></> : item.price}</p>
      {comboPrice && (
        <div style={{color:'#52c41a',fontWeight:500,marginBottom:8}}>Combo Offer Applied!</div>
      )}
      <button onClick={() => addToCart(comboPrice ? {...item, price: comboPrice} : item)}>
        Add to Cart
      </button>
    </div>
  );
}


export default ProductCard;
