import React, { useContext } from "react";
import ProductList from "../components/ProductList";
import { CartContext } from "../context/CartContext";

function Shop() {
  const { cartItems } = useContext(CartContext);
  const hasAlmondChocolate = cartItems.some(item => item.name && item.name.toLowerCase().includes("almond chocolate"));
  return (
    <div style={{ padding: "20px" }}>
      {hasAlmondChocolate && (
        <div style={{ background: "#e6fffb", borderRadius: 8, padding: 16, marginBottom: 20, textAlign: "center", boxShadow: "0 1px 4px rgba(0,0,0,0.05)" }}>
          <h4 style={{ margin: 0 }}>ðŸŽ‰ Special Offer!</h4>
          <p style={{ margin: "8px 0" }}>Because you have <b>Almond Chocolate</b> in your cart, <b>Mango Ice Cream</b> is now just <span style={{ color: "#ff4d4f" }}>â‚¹60</span>!</p>
        </div>
      )}
      <h2>Ice Creams</h2>
      <ProductList category="icecream" />
      <h2>Chocolates</h2>
      <ProductList category="chocolate" />
    </div>
  );
}

export default Shop;
