import React, { useContext } from "react";
import { CartContext } from "../context/CartContext";

function Cart() {
  const { cartItems, removeFromCart, addToCart } = useContext(CartContext);

  const total = cartItems.reduce((sum, item) => sum + item.price * item.qty, 0);

  // Combo offer logic: if Vanilla Ice Cream is in cart, show offer for Dark Chocolate at â‚¹80
  const hasVanillaIceCream = cartItems.some(item => item.name && item.name.toLowerCase().includes("vanilla ice cream"));
  const hasComboDarkChocolate = cartItems.some(item => item.id === 20001); // unique id for combo chocolate
  const darkChocolateCombo = {
    id: 20001,
    name: "Dark Chocolate (Combo Offer)",
    price: 80,
    image: "http://www.bestherbalhealth.com/wp-content/uploads/2015/02/Health-Benefits-of-Dark-Chocolate.jpg",
    category: "chocolate"
  };

  return (
    <div className="cart-container" style={{ maxWidth: 700, margin: "40px auto", background: "#fff", borderRadius: 16, boxShadow: "0 2px 8px rgba(0,0,0,0.1)", padding: 32 }}>
      <h2 style={{ textAlign: "center", marginBottom: 32, color: "#d4380d" }}>ðŸ›’ Your Cart</h2>
      {cartItems.length === 0 ? (
        <p style={{ textAlign: "center", color: "#888" }}>No items in cart.</p>
      ) : (
        <div>
          {hasVanillaIceCream && !hasComboDarkChocolate && (
            <div style={{ background: "#f0f5ff", borderRadius: 8, padding: 16, marginBottom: 20, textAlign: "center", boxShadow: "0 1px 4px rgba(0,0,0,0.05)" }}>
              <h4 style={{ margin: 0 }}>ðŸŽ‰ Combo Offer!</h4>
              <p style={{ margin: "8px 0" }}>Buy <b>Vanilla Ice Cream</b> and get <b>Dark Chocolate</b> for just <span style={{ color: "#ff4d4f" }}>â‚¹80</span>!</p>
              <button onClick={() => addToCart(darkChocolateCombo)} style={{ background: "#141414", color: "#fff", border: "none", borderRadius: 6, padding: "8px 16px", cursor: "pointer", fontWeight: 500 }}>Add Combo Dark Chocolate</button>
            </div>
          )}
          {cartItems.map((item) => (
            <div key={item.id} style={{ display: "flex", alignItems: "center", borderBottom: "1px solid #eee", padding: "18px 0" }}>
              <img src={item.image} alt={item.name} style={{ width: 90, height: 90, objectFit: "cover", borderRadius: 10, marginRight: 24, boxShadow: "0 1px 4px rgba(0,0,0,0.08)" }} />
              <div style={{ flex: 1 }}>
                <h4 style={{ margin: "0 0 8px 0", color: "#222" }}>{item.name}</h4>
                <div style={{ display: "flex", alignItems: "center", marginBottom: 8 }}>
                  <button onClick={() => item.qty > 1 && addToCart({ ...item, qty: item.qty - 1 })} style={{ padding: "2px 8px", fontSize: 18, borderRadius: 4, border: "1px solid #ccc", background: "#f5f5f5", cursor: "pointer" }}>-</button>
                  <input type="number" min={1} value={item.qty} onChange={e => {
                    const value = Math.max(1, Number(e.target.value));
                    addToCart({ ...item, qty: value });
                  }} style={{ width: 40, textAlign: "center", margin: "0 8px", borderRadius: 4, border: "1px solid #ccc" }} />
                  <button onClick={() => addToCart({ ...item, qty: item.qty + 1 })} style={{ padding: "2px 8px", fontSize: 18, borderRadius: 4, border: "1px solid #ccc", background: "#f5f5f5", cursor: "pointer" }}>+</button>
                </div>
                <p style={{ margin: "4px 0 0 0", color: "#555" }}>Price: â‚¹{item.price} Ã— {item.qty} = <b>â‚¹{item.price * item.qty}</b></p>
              </div>
              <button onClick={() => removeFromCart(item.id)} style={{ background: "#ff4d4f", color: "#fff", border: "none", borderRadius: 8, padding: "10px 18px", cursor: "pointer", fontWeight: 500 }}>Remove</button>
            </div>
          ))}
          <h3 style={{ textAlign: "right", marginTop: 32, color: "#222" }}>Total: â‚¹{total}</h3>
        </div>
      )}
    </div>
  );
}

export default Cart;
