import React, { useContext } from "react";
import { CartContext } from "../context/CartContext";

function ProductCard({ item }) {
  const { addToCart } = useContext(CartContext);
  const navigate = require('react-router-dom').useNavigate();
  // ...existing code...
  return (
    <div style={{
      border: "none",
      boxShadow: "0 2px 12px rgba(255,174,94,0.12)",
      padding: "18px 16px 24px 16px",
      margin: "18px 10px",
      width: "220px",
      borderRadius: "18px",
      background: "linear-gradient(120deg,#fffbe6 80%,#ffe29f 100%)",
      display: "flex",
      flexDirection: "column",
      alignItems: "center"
    }}>
      <img src={item.image} alt={item.name} style={{ width: "100%", height: 120, objectFit: "cover", borderRadius: 12, marginBottom: 12, boxShadow: "0 2px 8px #ffe29f" }} />
      <h3 style={{ color: "#d4380d", fontSize: 20, fontWeight: 700, margin: "0 0 8px 0", textAlign: "center", letterSpacing: 1 }}>{item.name}</h3>
      <p style={{ color: "#222", fontSize: 18, fontWeight: 600, marginBottom: 16 }}>â‚¹{item.price}</p>
      <button
        onClick={() => {
          addToCart({ ...item, qty: 1 });
          navigate('/cart');
        }}
        style={{
          background: "linear-gradient(90deg,#ff4d4f 0%,#ffa99f 100%)",
          color: "#fff",
          border: "none",
          borderRadius: 10,
          padding: "10px 24px",
          fontWeight: 700,
          fontSize: 17,
          cursor: "pointer",
          boxShadow: "0 2px 8px #ffa99f",
          marginTop: 8,
          transition: "background 0.2s"
        }}
        onMouseOver={e => e.currentTarget.style.background = "linear-gradient(90deg,#ffa99f 0%,#ff4d4f 100%)"}
        onMouseOut={e => e.currentTarget.style.background = "linear-gradient(90deg,#ff4d4f 0%,#ffa99f 100%)"}
      >Add to Cart</button>
    </div>
  );
}


export default ProductCard;
