import React, { useContext } from "react";
import ProductList from "../components/ProductList";
import { CartContext } from "../context/CartContext";

function Shop() {
  const { cartItems } = useContext(CartContext);
  const hasAlmondChocolate = cartItems.some(item => item.name && item.name.toLowerCase().includes("almond chocolate"));
  return (
    <div style={{ minHeight: "100vh", background: "linear-gradient(135deg,#fffbe6 0%,#fbecc4 100%)", padding: "40px 0" }}>
      <div style={{ maxWidth: 1200, margin: "0 auto", padding: "0 20px" }}>
        <h1 style={{ textAlign: "center", fontSize: "2.5rem", fontWeight: "bold", color: "#d4380d", marginBottom: 32 }}>Shop Our Sweets</h1>
        {hasAlmondChocolate && (
          <div style={{ background: "#e6fffb", borderRadius: 8, padding: 16, marginBottom: 32, textAlign: "center", boxShadow: "0 1px 4px rgba(0,0,0,0.05)" }}>
            <h4 style={{ margin: 0 }}>üéâ Special Offer!</h4>
            <p style={{ margin: "8px 0" }}>Because you have <b>Almond Chocolate</b> in your cart, <b>Mango Ice Cream</b> is now just <span style={{ color: "#ff4d4f" }}>‚Çπ60</span>!</p>
          </div>
        )}
        <div style={{ display: "flex", gap: 40, flexWrap: "wrap", justifyContent: "center" }}>
          <div style={{ background: "#fff", borderRadius: 16, boxShadow: "0 2px 8px rgba(0,0,0,0.08)", padding: 32, minWidth: 340, flex: 1, maxWidth: 500 }}>
            <h2 style={{ color: "#d4380d", marginBottom: 16, textAlign: "center" }}>üç¶ Ice Creams</h2>
            <ProductList category="icecream" />
          </div>
          <div style={{ background: "#fff", borderRadius: 16, boxShadow: "0 2px 8px rgba(0,0,0,0.08)", padding: 32, minWidth: 340, flex: 1, maxWidth: 500 }}>
            <h2 style={{ color: "#d4380d", marginBottom: 16, textAlign: "center" }}>üç´ Chocolates</h2>
            <ProductList category="chocolate" />
          </div>
        </div>
      </div>
    </div>
  );
}

export default Shop;
