import ProductList from "../components/ProductList";

function Shop() {
  return (
    <div style={{
      minHeight: "100vh",
      background: "linear-gradient(135deg,#ffe29f 0%,#ffa99f 50%,#fffde4 100%)",
      padding: "40px 0"
    }}>
      <div style={{ maxWidth: 1200, margin: "0 auto", padding: "0 24px" }}>
        <h1 style={{ textAlign: "center", color: "#d4380d", fontSize: 44, fontWeight: 800, marginBottom: 48, letterSpacing: 2, textShadow: "0 2px 8px #fffbe6" }}>Welcome to Sweet Shop</h1>
        <div style={{ background: "#fff", borderRadius: 24, boxShadow: "0 4px 24px rgba(0,0,0,0.10)", padding: 40, marginBottom: 36 }}>
          <h2 style={{ color: "#ff4d4f", fontSize: 32, fontWeight: 700, marginBottom: 32, textAlign: "center", letterSpacing: 1, textShadow: "0 2px 8px #ffe29f" }}>All Products</h2>
          <div style={{ display: "flex", flexDirection: "row", gap: 24, overflowX: "auto", paddingBottom: 10 }}>
            <ProductList />
          </div>
        </div>
      </div>
    </div>
  );
}

export default Shop;
