import React, { useContext } from "react";
import { CartContext } from "../context/CartContext";

function Cart() {
  const { cartItems, removeFromCart } = useContext(CartContext);

  const total = cartItems.reduce((sum, item) => sum + item.price * item.qty, 0);

  return (
    <div className="cart-container" style={{ maxWidth: 700, margin: "40px auto", background: "#fff", borderRadius: 16, boxShadow: "0 2px 8px rgba(0,0,0,0.1)", padding: 32 }}>
      <h2 style={{ textAlign: "center", marginBottom: 32, color: "#d4380d" }}>ðŸ›’ Your Cart</h2>
      {cartItems.length === 0 ? (
        <p style={{ textAlign: "center", color: "#888" }}>No items in cart.</p>
      ) : (
        <div>
          {cartItems.map((item) => (
            <div key={item.id} style={{ display: "flex", alignItems: "center", borderBottom: "1px solid #eee", padding: "18px 0" }}>
              <img src={item.image} alt={item.name} style={{ width: 90, height: 90, objectFit: "cover", borderRadius: 10, marginRight: 24, boxShadow: "0 1px 4px rgba(0,0,0,0.08)" }} />
              <div style={{ flex: 1 }}>
                <h4 style={{ margin: "0 0 8px 0", color: "#222" }}>{item.name}</h4>
                <p style={{ margin: 0, color: "#555" }}>Quantity: <b>{item.qty}</b></p>
                <p style={{ margin: "4px 0 0 0", color: "#555" }}>Price: â‚¹{item.price} Ã— {item.qty} = <b>â‚¹{item.price * item.qty}</b></p>
              </div>
              <button onClick={() => removeFromCart(item.id)} style={{ background: "#ff4d4f", color: "#fff", border: "none", borderRadius: 8, padding: "10px 18px", cursor: "pointer", fontWeight: 500 }}>Remove</button>
            </div>
          ))}
          <h3 style={{ textAlign: "right", marginTop: 32, color: "#222" }}>Total: â‚¹{total}</h3>
        </div>
      )}
    </div>
  );
}

export default Cart;
