  // Combo offer logic: if Almond Chocolate is in cart, show offer for Mango Ice Cream at ₹60
  const hasAlmondChocolate = cartItems.some(item => item.name && item.name.toLowerCase().includes("almond chocolate"));
  const hasComboMangoIceCream = cartItems.some(item => item.id === 50001); // unique id for combo ice cream
  const mangoIceCreamCombo = {
    id: 50001,
    name: "Mango Ice Cream (Combo Offer)",
    price: 60,
    image: "https://www.cookwithkushi.com/wp-content/uploads/2021/04/IMG_3405_new-768x1152.jpg",
    category: "icecream"
  };
import React, { useContext } from "react";
import { CartContext } from "../context/CartContext"

function Cart() {
  const { cartItems, removeFromCart } = useContext(CartContext);

  const total = cartItems.reduce((sum, item) => sum + item.price * item.qty, 0);

  // Combo offer logic: if Chocolate Ice Cream is in cart, show offer for Belgian Chocolate at ₹90
  const hasChocolateIceCream = cartItems.some(item => item.name && item.name.toLowerCase().includes("chocolate ice cream"));
  const hasComboBelgianChocolate = cartItems.some(item => item.id === 30001); // unique id for combo chocolate
  const belgianChocolateCombo = {
    id: 30001,
    name: "Belgian Chocolate (Combo Offer)",
    price: 90,
    image: "https://tse1.mm.bing.net/th/id/OIP.5kletYVpG-GaGnJtkmuwxQHaE8?rs=1&pid=ImgDetMain&o=7&rm=3",
    category: "chocolate"
  };

  // Combo offer logic: if Strawberry Ice Cream is in cart, show offer for KitKat Chocolate at ₹45
  const hasStrawberryIceCream = cartItems.some(item => item.name && item.name.toLowerCase().includes("strawberry ice cream"));
  const hasComboKitkatChocolate = cartItems.some(item => item.id === 40001); // unique id for combo chocolate
  const kitkatChocolateCombo = {
    id: 40001,
    name: "KitKat Chocolate (Combo Offer)",
    price: 45,
    image: "https://www.kitkat.com/sites/g/files/fnmzdf2461/files/2023-03/KitKat%20Bar%20-%20Product.png",
    category: "chocolate"
  };
  const { addToCart } = useContext(CartContext);

  return (
    <div className="cart-container" style={{ maxWidth: 600, margin: "40px auto", background: "#fff", borderRadius: 12, boxShadow: "0 2px 8px rgba(0,0,0,0.1)", padding: 24 }}>
      <h2 style={{ textAlign: "center", marginBottom: 24 }}>🛒 Your Cart</h2>
      {cartItems.length === 0 ? (
        <p style={{ textAlign: "center", color: "#888" }}>No items in cart.</p>
      ) : (
        <div>
          {hasChocolateIceCream && !hasComboBelgianChocolate && (
            <div style={{ background: "#e6f7ff", borderRadius: 8, padding: 16, marginBottom: 20, textAlign: "center", boxShadow: "0 1px 4px rgba(0,0,0,0.05)" }}>
              <h4 style={{ margin: 0 }}>🎉 Combo Offer!</h4>
              <p style={{ margin: "8px 0" }}>Buy <b>Chocolate Ice Cream</b> and get <b>Belgian Chocolate</b> for just <span style={{ color: "#ff4d4f" }}>₹90</span>!</p>
              <button onClick={() => addToCart(belgianChocolateCombo)} style={{ background: "#52c41a", color: "#fff", border: "none", borderRadius: 6, padding: "8px 16px", cursor: "pointer", fontWeight: 500 }}>Add Combo Chocolate</button>
            </div>
          )}
          {hasStrawberryIceCream && !hasComboKitkatChocolate && (
            <div style={{ background: "#fffbe6", borderRadius: 8, padding: 16, marginBottom: 20, textAlign: "center", boxShadow: "0 1px 4px rgba(0,0,0,0.05)" }}>
              <h4 style={{ margin: 0 }}>🎉 Combo Offer!</h4>
              <p style={{ margin: "8px 0" }}>Buy <b>Strawberry Ice Cream</b> and get <b>KitKat Chocolate</b> for just <span style={{ color: "#ff4d4f" }}>₹45</span>!</p>
              <button onClick={() => addToCart(kitkatChocolateCombo)} style={{ background: "#d4380d", color: "#fff", border: "none", borderRadius: 6, padding: "8px 16px", cursor: "pointer", fontWeight: 500 }}>Add Combo KitKat</button>
            </div>
          )}
          {hasAlmondChocolate && !hasComboMangoIceCream && (
            <div style={{ background: "#e6fffb", borderRadius: 8, padding: 16, marginBottom: 20, textAlign: "center", boxShadow: "0 1px 4px rgba(0,0,0,0.05)" }}>
              <h4 style={{ margin: 0 }}>🎉 Combo Offer!</h4>
              <p style={{ margin: "8px 0" }}>Buy <b>Almond Chocolate</b> and get <b>Mango Ice Cream</b> for just <span style={{ color: "#ff4d4f" }}>₹60</span>!</p>
              <button onClick={() => addToCart(mangoIceCreamCombo)} style={{ background: "#faad14", color: "#fff", border: "none", borderRadius: 6, padding: "8px 16px", cursor: "pointer", fontWeight: 500 }}>Add Combo Mango Ice Cream</button>
            </div>
          )}
          {cartItems.map((item) => (
            <div key={item.id} style={{ display: "flex", alignItems: "center", borderBottom: "1px solid #eee", padding: "16px 0" }}>
              <img src={item.image} alt={item.name} style={{ width: 80, height: 80, objectFit: "cover", borderRadius: 8, marginRight: 20, boxShadow: "0 1px 4px rgba(0,0,0,0.08)" }} />
              <div style={{ flex: 1 }}>
                <h4 style={{ margin: "0 0 8px 0" }}>{item.name}</h4>
                <p style={{ margin: 0, color: "#555" }}>Quantity: <b>{item.qty}</b></p>
                <p style={{ margin: "4px 0 0 0", color: "#555" }}>Price: ₹{item.price} × {item.qty} = <b>₹{item.price * item.qty}</b></p>
              </div>
              <button onClick={() => removeFromCart(item.id)} style={{ background: "#ff4d4f", color: "#fff", border: "none", borderRadius: 6, padding: "8px 16px", cursor: "pointer", fontWeight: 500 }}>Remove</button>
            </div>
          ))}
          <h3 style={{ textAlign: "right", marginTop: 24, color: "#222" }}>Total: ₹{total}</h3>
        </div>
      )}
    </div>
  );
}

export default Cart;
